import React, { Component } from "react";
import { connect } from "react-redux";
import { addNewUser } from "../../../dataflow/modules/SignUpReducer";
import styled from "styled-components";
import VisibilityOff from "../../../assets/visibility-off.svg";
import VisibilityOn from "../../../assets/visibility-on.svg";

import Button from "../../../components/Button";
import Input from "../../../components/Input";
import ImageLogo from "../../../components/ImageLogo";
import ModalSucess from "./ModalSucess";

const mapStateToProps = state => {
  return {
    users: state.users
  };
};

const mapDispatchToProps = dispatch => ({
  addNewUser: user => {
    dispatch(addNewUser(user));
  }
});

export const Form = styled.form`
  min-width: 40%;
  min-height: 100vh;
  background-color: #fff;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 1rem 2rem;
  margin: 1rem 0;

  h1 {
    font-size: 1.4rem;
    font-family: "Overpass", ExtraBold;
    text-transform: uppercase;
    margin: 2rem 0 1.5rem 2.5rem;
    align-self: flex-start;
  }
  @media (max-width: 425px) {
    h1 {
      margin: 2rem 0;
      font-size: 1.3rem;
    }
  }

  input {
    border: ${props => props.withError && "1px solid red"};
  }

  @media (max-width: 375px) {
    padding: 1rem;
  }

  span {
    width: 90%;
    display: flex;
    justify-content: space-between;
  }
  @media (max-width: 425px) {
    span {
      width: 100%;
      flex-direction: column;
    }
    p {
      text-align: center;
    }
  }

  p {
    width: 80%;
    font-size: 0.65rem;
    margin: 0.5rem 0;
    color: #505050;
    font-family: Overpass, Regular;

    strong {
      color: #85144b;
      font-size: 0.65rem;
      cursor: pointer;
      text-decoration: underline;
      margin: 0 0.2rem;
      font-family: Overpass, Regular;
    }
  }
`;

export const Label = styled.label`
  position: relative;
  width: 90%;
  display: flex;
  align-items: center;
  flex-direction: column;

  @media (max-width: 425px) {
    width: 100%;
  }
  p {
    align-self: flex-start;
    text-transform: uppercase;
    color: #85144b;
    font-size: 0.7rem;
    margin-left: 1.3rem;
    font-family: Overpass, Bold;
    font-weight: bold;
  }

  @media (max-width: 425px) {
    p {
      text-align: left;
      margin-left: 0;
    }
  }
`;

export const Error = styled.span`
  font-size: 0.6rem;
  color: red;
  align-self: flex-start;
  font-weight: normal;
  margin-left: 1.4rem;
`;
export const InputForm = styled.input`
  width: 90%;
  height: 40px;
  border: 1px solid #ffcfcd;
  background: #fafafa;
  padding: 0.5rem;
  font-size: 0.9rem;

  @media (max-width: 425px) {
    width: 100%;
  }
`;

export const ImagePassword = styled.img`
  position: absolute;
  bottom: 2vh;
  right: 1.5rem;
  cursor: pointer;
`;

class Formulario extends Component {
  state = {
    togglePassword: false,
    modalSucess: false,
    user: {
      nome: "",
      sobrenome: "",
      rg: "",
      orgao: "",
      uf: "",
      nascimento: "",
      cpf: "",
      email: "",
      telefone: "",
      senha: ""
    },
    isErrorRg: false,
    isErrorSenha: false,
    isErrorCpf: false,
    isEmpty: false
  };

  togglePassword = ev => {
    ev.preventDefault();
    this.setState({
      togglePassword: !this.state.togglePassword
    });
  };

  handleModalSucess = () => {
    this.setState({
      modalSucess: !this.state.modalSucess
    });
  };

  handleChange = (field, ev) => {
    const { user } = this.state;
    user[field] = ev.target.value;
    this.setState({ user });
  };

  handleSubmit = ev => {
    ev.preventDefault();
    this.errors();
  };

  errors = () => {
    const { user } = this.state;
    const {
      nome,
      sobrenome,
      rg,
      orgao,
      uf,
      nascimento,
      cpf,
      email,
      telefone,
      senha
    } = this.state.user;
    if (senha.length < 4) {
      this.setState({ isErrorSenha: true });
    } else {
      this.setState({ isErrorSenha: false });
    }
    if (rg.length < 9 || rg.length > 9) {
      this.setState({ isErrorRg: true });
    } else {
      this.setState({ isErrorRg: false });
    }
    if (cpf.length < 11 || cpf.length > 11) {
      this.setState({ isErrorCpf: true });
    } else {
      this.setState({ isErrorCpf: false });
    }
    if (
      nome === "" ||
      sobrenome === "" ||
      rg === "" ||
      orgao === "" ||
      uf === "" ||
      nascimento === "" ||
      cpf === "" ||
      email === "" ||
      telefone === "" ||
      senha === ""
    ) {
      this.setState({ isEmpty: true });
    } else {
      this.setState({ isEmpty: false });
      this.props.addNewUser(user);
      this.handleModalSucess();
    }
  };

  render() {
    let errorMessage = [
      "RG inválido",
      "Senha fraca",
      "CPF inválido",
      "Preencha todos os valores"
    ];

    const {
      isErrorSenha,
      isErrorRg,
      isErrorCpf,
      modalSucess,
      isEmpty
    } = this.state;

    return (
      <>
        {modalSucess === true ? (
          <ModalSucess handleModalSucess={this.handleModalSucess} />
        ) : (
          <Form onSubmit={this.handleSubmit}>
            <ImageLogo />
            <h1>cadastrar pessoa física</h1>
            <Label>
              <p>Nome:</p>
              <Input
                type="text"
                onChange={ev => this.handleChange("nome", ev)}
                value={this.state.nome}
                placeholder="Nome"
                name="nome"
              />
              {isEmpty && <Error>{errorMessage[3]}</Error>}
            </Label>
            <Label>
              <p>Sobrenome:</p>
              <Input
                type="text"
                onChange={ev => this.handleChange("sobrenome", ev)}
                value={this.state.sobrenome}
                placeholder="Sobrenome"
                name="sobrenome"
              />
              {isEmpty && <Error>{errorMessage[3]}</Error>}
            </Label>
            <span>
              <Label>
                <p>rg</p>
                <InputForm
                  type="number"
                  onChange={ev => this.handleChange("rg", ev)}
                  value={this.state.rg}
                  placeholder="000000-0"
                  name="rg"
                />
                {isErrorRg && <Error>{errorMessage[0]}</Error>}
              </Label>
              <Label>
                <p>Orgão expedidor</p>
                <InputForm
                  type="text"
                  onChange={ev => this.handleChange("orgao", ev)}
                  value={this.state.orgao}
                  name="orgao"
                />
                {isEmpty && <Error>{errorMessage[3]}</Error>}
              </Label>
            </span>
            <span>
              <Label>
                <p>uf</p>
                <InputForm
                  type="text"
                  onChange={ev => this.handleChange("uf", ev)}
                  value={this.state.uf}
                  name="uf"
                />
                {isEmpty && <Error>{errorMessage[3]}</Error>}
              </Label>
              <Label>
                <p>data de nascimento</p>
                <InputForm
                  type="number"
                  onChange={ev => this.handleChange("nascimento", ev)}
                  value={this.state.nascimento}
                  placeholder="02/01/2020"
                  name="nascimento"
                />
                {isEmpty && <Error>{errorMessage[3]}</Error>}
              </Label>
            </span>
            <Label>
              <p>cpf</p>
              <Input
                type="number"
                onChange={ev => this.handleChange("cpf", ev)}
                value={this.state.cpf}
                placeholder="000000-0"
                name="cpf"
              />

              {isErrorCpf && <Error>{errorMessage[2]}</Error>}
            </Label>
            <Label>
              <p>email</p>
              <Input
                type="email"
                onChange={ev => this.handleChange("email", ev)}
                value={this.state.email}
                name="email"
                placeholder="nome@mail.com"
                require
              />

              {isEmpty && <Error>{errorMessage[3]}</Error>}
            </Label>
            <Label>
              <p>telefone</p>
              <Input
                type="tel"
                onChange={ev => this.handleChange("telefone", ev)}
                value={this.state.telefone}
                placeholder="(00) 00000-0000"
                name="telefone"
              />
              {isEmpty && <Error>{errorMessage[3]}</Error>}
            </Label>
            <Label>
              <p>senha</p>
              <Input
                className="input-password"
                type={this.state.togglePassword === true ? "text" : "password"}
                onChange={ev => this.handleChange("senha", ev)}
                value={this.state.senha}
                placeholder="Inserir senha"
                name="password"
              />
              {this.state.togglePassword === true ? (
                <span>
                  <ImagePassword
                    src={VisibilityOn}
                    onClick={this.togglePassword}
                  />
                </span>
              ) : (
                <span>
                  <ImagePassword
                    src={VisibilityOff}
                    onClick={this.togglePassword}
                  />
                </span>
              )}
              {isErrorSenha && <Error>{errorMessage[1]}</Error>}
            </Label>
            <p>
              Clique abaixo para concordar com os
              <strong onClick={this.props.handleModalTerms}>
                Termos de Serviço
              </strong>
              e registrar.
            </p>
            <Button
              text="concordar e criar conta"
              type="submit"
              onClick={this.handleSubmit}
            />
          </Form>
        )}
      </>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Formulario);
