// Libs
import React from 'react';
import styled from 'styled-components';

// Components
import ImageLogo from '../../../components/ImageLogo';
import Input from '../../../components/Input';
import Button from '../../../components/Button';


export const ContainerForm = styled.div`
  height: 100vh;
  background-color: #FFCFCD;
  display: flex;
  align-items: center;
	justify-content: center;
  margin: 0;
  padding: 5rem;
`;

export const Form = styled.form`
  width: 50%;
  background-color: #fff;
  display: flex;
  align-items: center;
  flex-direction: column;
`;

export const Title = styled.h1`
	color: #purple;

`;

export const VoltarLogin = styled.span` 
  width: 95%;
  display: flex;
  align-items: center;
  flex-direction: row;
  justify-content: space-around;
  margin: 1rem;

  buttonText {
    color: #85144B;
    font-size: 1rem; 
		font-family: Eurostile, Regular; 
  }
`;


class PasswordResetCodeScreen extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			value: '',
			email: '',
			password: '',
			type: 'password',
		};
	}

	handleChangeNumber = (ev) => {
		this.setState({
			number: ev.target.value,
		});
	};

	handleChangePassword = (ev) => {
		this.setState({
			password: ev.target.value,
		});
	}

	render() {
		console.log(this.state.email);
		return (
			<ContainerForm>
				<Form onSubmit={this.handleSubmit}>
					<ImageLogo />
					<Title>PASSWORD RESET</Title>
					<p>A confirmation code sent to name@email.com, please, paste it bellow:</p>
					<span>
						<label>CONFIRMATION CODE</label>
						<input
							login
							onChange={this.handleChangeNumber}
							placeholder="Insert here the code"
						/>
					</span>
					<span>
						<label>NEW PASSWORD</label>
						<input
							login
							type={this.state.type}
							onChange={this.handleChangePassword}
							placeholder="Insert here the code"
						/>
					</span>
					<span>
						<label>REPEAT NEW PASSWORD</label>
						<input
							login
							type={this.state.type}
							onChange={this.handleChangePassword}
							placeholder="Insert here the code"
						/>
					</span>
					<button
						login
						text="PROCEED WITH NEW PASSWORD"
						type="submit"
					/>
					<VoltarLogin>
						<buttonText>RESEND EMAIL</buttonText>
					</VoltarLogin>
				</Form>
			</ContainerForm>
		);
	}
}

export default PasswordResetCodeScreen;
