import React, { Component } from "react";
import { connect } from "react-redux";
import styled from "styled-components";
import VisibilityOff from "../../../assets/visibility-off.svg";
import VisibilityOn from "../../../assets/visibility-on.svg";

import Button from "../../../components/Button";
import Input from "../../../components/Input";
import ImageLogo from "../../../components/ImageLogo";
import ModalSucess from "./ModalSucess";

export const Form = styled.form`
  min-width: 30%;
  min-height: 100vh;
  background-color: #fff;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 1rem 2rem;
  margin: 1rem 0;

  h1 {
    font-size: 20px;
    text-transform: uppercase;
    margin: 2rem 0 1rem 2rem;
    align-self: flex-start;
  }

  input {
    border: ${props => props.withError && "1px solid red"};
  }

  @media (max-width: 375px) {
    padding: 1rem;
  }

  span {
    width: 90%;
    display: flex;
    justify-content: space-between;
  }
  @media (max-width: 425px) {
    span {
      width: 100%;
      flex-direction: column;
    }
    p {
      text-align: center;
    }
  }

  p {
    width: 80%;
    font-size: 11px;
    margin: 0.5rem 0;

    strong {
      color: #85144b;
      cursor: pointer;
      text-decoration: underline;
      margin: 0 0.3rem;
    }
  }
`;

export const Label = styled.label`
  position: relative;
  width: 90%;
  display: flex;
  align-items: center;
  flex-direction: column;

  @media (max-width: 425px) {
    width: 100%;
  }
  p {
    align-self: flex-start;
    text-transform: uppercase;
    color: #85144b;
    font-size: 13px;
    margin-left: 1rem;
  }

  @media (max-width: 425px) {
    p {
      text-align: left;
      margin-left: 0;
    }
  }
`;

export const Error = styled.h4`
  font-size: 11px;
  color: red;
  text-align: left;
  font-weight: normal;
  margin: 0.2rem 0;
`;
export const InputForm = styled.input`
  width: 90%;
  height: 40px;
  border: 1px solid #ffcfcd;
  background: #fafafa;
  padding: 0.5rem;
  font-size: 15px;

  @media (max-width: 425px) {
    width: 100%;
  }
`;

export const ImagePassword = styled.img`
  position: absolute;
  bottom: 1.5vh;
  right: 2vw;
  cursor: pointer;
`;

class Formulario extends Component {
  state = {
    rg: "",
    cpf: "",
    password: "",
    modalSucess: false,
    togglePassword: false,
    empty: false,
    isErrorRg: false,
    isErrorCpf: false,
    isErrorPassword: false
  };

  togglePassword = ev => {
    ev.preventDefault();
    this.setState({
      togglePassword: !this.state.togglePassword
    });
  };

  handleModalSucess = () => {
    this.setState({
      modalSucess: !this.state.modalSucess
    });
  };

  handleChange = ev => {
    this.setState({
      [ev.target.name]: ev.target.value
    });
  };

  handleSubmit = ev => {
    // ev.preventDefault();
    console.log("eai");
  };

  render() {
    return (
      <>
        {this.state.modalSucess === true ? (
          <ModalSucess handleModalSucess={this.handleModalSucess} />
        ) : (
          <Form
            onSubmit={this.handleSubmit}
            withError={
              (this.state.empty,
              this.state.isErrorCpf,
              this.state.isErrorPassword,
              this.state.isErrorRg)
            }
          >
            <ImageLogo />
            <h1>cadastrar pessoa física</h1>
            <Label>
              <p>Nome:</p>
              <Input
                type="text"
                onChange={this.handleChange}
                value={this.state.nome}
                placeholder="Nome"
                name="nome"
              />
            </Label>
            <Label>
              <p>Sobrenome:</p>
              <Input
                type="text"
                onChange={this.handleChange}
                value={this.state.sobrenome}
                placeholder="Sobrenome"
                name="sobrenome"
              />
            </Label>
            <span>
              <Label>
                <p>rg</p>
                <InputForm
                  type="number"
                  onChange={this.handleChange}
                  value={this.state.rg}
                  placeholder="000000-0"
                  name="rg"
                />
                {this.state.isErrorRg && <Error>RG invalido</Error>}
              </Label>
              <Label>
                <p>Orgão expedidor</p>
                <InputForm
                  type="text"
                  onChange={this.handleChange}
                  value={this.state.orgao}
                  name="orgao"
                />
              </Label>
            </span>
            <span>
              <Label>
                <p>uf</p>
                <InputForm
                  type="text"
                  onChange={this.handleChange}
                  value={this.state.uf}
                  name="uf"
                />
              </Label>
              <Label>
                <p>data de nascimento</p>
                <InputForm
                  type="number"
                  onChange={this.handleChange}
                  value={this.state.nascimento}
                  placeholder="02/01/2020"
                  name="nascimento"
                />
              </Label>
            </span>
            <Label>
              <p>cpf</p>
              <Input
                type="number"
                onChange={this.handleChange}
                value={this.state.cpf}
                placeholder="000000-0"
                name="cpf"
              />
              {this.state.isErrorCpf && <Error>CPF inválido</Error>}
            </Label>
            <Label>
              <p>email</p>
              <Input
                type="email"
                onChange={this.handleChange}
                value={this.state.email}
                name="email"
              />
            </Label>
            <Label>
              <p>telefone</p>
              <Input
                type="tel"
                onChange={this.handleChange}
                value={this.state.telefone}
                placeholder="(00) 00000-0000"
                name="telefone"
              />
            </Label>
            <Label>
              <p>senha</p>
              <Input
                className="input-password"
                type={this.state.togglePassword === true ? "text" : "password"}
                onChange={this.handleChange}
                value={this.state.password}
                placeholder="Inserir senha"
                name="password"
              />
              {this.state.togglePassword === true ? (
                <ImagePassword
                  src={VisibilityOn}
                  onClick={this.togglePassword}
                />
              ) : (
                <ImagePassword
                  src={VisibilityOff}
                  onClick={this.togglePassword}
                />
              )}
              {this.state.isErrorPassword && <Error>Senha fraca</Error>}
            </Label>
            <p>
              Clique abaixo para concordar com os
              <strong onClick={this.props.handleModalTerms}>
                Termos de Serviço
              </strong>{" "}
              e registrar.
            </p>
            <Button
              text="concordar e criar conta"
              type="button"
              onCliick={this.handleSubmit}
            />
          </Form>
        )}
      </>
    );
  }
}

export default connect()(Formulario);
