// Libs
import React from 'react';
import styled from 'styled-components';

// Components
import ImageLogo from '../../../components/ImageLogo';

export const ContainerForm = styled.div`
  width: 100%;
  height: 100vh;
  background-color: #FFCFCD;
  display: flex;
  align-items: center;
  flex-direction: column; 
  margin: 0;
  padding: 5rem;

  @media (max-width: 648px) {
			background-color: #fff;
	 	}
	}
`;

export const Form = styled.form`
  width: 40%;
  height: 40vh;
  background-color: #fff;
  display: flex;
  align-items: center;
  flex-direction: column;
  justify-content: space-evenly;

  @media (max-width: 980px) {
			width: 40%;
	 	}
	}

	@media (max-width: 786px) {
			width: 45%;
	 	}
	}

	@media (max-width: 648px) {
			width: 90%;
			margin: 0;
	 	}
	}
`;

export const Title = styled.h1`
  width: 63%;
  color: #85144B;
  font-size: 1rem;
  font-weight: bold;
  margin-top: 0.9rem;
  display: flex;
  align-items: center;
  justify-content: center;
`;


export const BoxErro = styled.div`
	width: 62%;
	display: flex;
	flex-direction: column;
`;

export const Erro = styled.p`
  color: #FF4136;
  font-size: 2.5rem;
  font-weight: bold;
  display: flex;
  align-items: center;
  justify-content: center;
`;

export const Span = styled.span` 
  color: #231F20;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
`;

export const SpanParagraph = styled.span`
  width: 80%;
  font-size: 0.8rem;
  opacity: 1;   
`;



class ResetPasswordCode extends React.Component {
	render() {
		return (
			<ContainerForm>
				<ImageLogo loginScreen />
				<Form onSubmit={this.handleSubmit}>
					<Title>Aqui está o seu código de redefinição de senha:</Title>
					<BoxErro>
						<Erro>210704</Erro>
					</BoxErro>
					<Span>
						<SpanParagraph>Uma redefinição de senha foi necessária para o seu endereço de e-mail: nome@email.com.
               Se você não realizou essa solicitação,
               pode acessar sua conta normalmente.</SpanParagraph>
					</Span>
				</Form>
			</ContainerForm>
		);
	}
}

export default ResetPasswordCode;
