import React from 'react';
import styled from 'styled-components';


import ImageLogo from '../../../components/ImageLogo';
import Input from '../../../components/Input';
import Button from '../../../components/Button';

import VisibilityOn from '../../../assets/visibility-on.svg';
import VisibilityOff from '../../../assets/visibility-off.svg';

export const ContainerForm = styled.div`
  background-color: #FFCFCD;
  display: flex;
  align-items: flex-end;
  flex-direction: column; 
  margin: 0;

  @media (max-width: 648px) {
	background-color: #fff;
	  }
	}
`;

export const Form = styled.form`
  width: 50%;
  height: 100vh;
  background-color: #fff;
  display: flex;
  align-items: center;
  flex-direction: column;
  justify-content: center;

  @media (max-width: 648px) {
	width: 100%;
	  }
	}
`;

export const InputBox = styled.span`
	width: 62%;
	display: flex;
	flex-direction: column;
	position: relative;

	@media (max-width: 648px) {
		width: 70%;
		  }
		}

	@media (max-width: 320px) {
		width: 83%;
		  }
		}
`;


export const ImagePassword = styled.img`
  position: absolute;
  bottom: ${props => props.off ? '1rem' : '0.875rem'};
  right: 0.7rem;
  cursor: pointer;
`;

export const Label = styled.label`
  color: #85144B;
  font-size: 0.7rem;
  font-weight: bold;
  margin-top: 1rem;
  margin-bottom: 0.5rem;
`;

export const Span = styled.span` 
  width: 98%;
  display: flex;
  align-items: center;
  flex-direction: row;
  justify-content: space-around;
  margin-top: 0.5rem;

  buttonText {
    color: #85144B;
    font-size: 0.75rem; 
  }
`;

export const Error = styled.h2`
  width: 43%;
  display: flex;
  color: #D53B40;
  font-family: Eurostile, Medium;
  font-size: 0.75rem;
  align-self: flex-end;

  @media (max-width: 648px) {
		width: 40%;
		  }
		}
`;

class Login extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			value: '',
			email: '',
			password: '',
			error: false,
			type: 'password',
		};
	}

	handleChangeType = () => {
		this.setState({
			type: this.state.type === 'password' ? 'text' : 'password',
		});
	}

	handleSubmit = (ev) => {
		ev.preventDefault();
		if (this.state.email !== this.state.email2) {
			this.setState({
				error: true,
			});
		} else {
			this.setState({
				error: false,

			});
		}
	}

	handleChangeEmail = (ev) => {
		this.setState({
			email: ev.target.value,
		});
	};

	handleChangePassword = (ev) => {
		this.setState({
			password: ev.target.value,
		});
	}


	render() {
		console.log(this.state.email);
		return (
			<ContainerForm>
				<Form onSubmit={this.handleSubmit}>
					<ImageLogo />
					<InputBox>
						<Label>E-MAIL</Label>
						<Input
							login
							type="email"
							onChange={this.handleChangeEmail}
							placeholder="name@email.com"
						/>
					</InputBox>
					<InputBox>
						<Label>SENHA</Label>
						<Input
							login
							type={this.state.type}
							onChange={this.handleChangePassword}
							placeholder="Insert password"
						/>
						<span>
							<ImagePassword
								src={this.state.type === 'password' ? VisibilityOn : VisibilityOff}
								onClick={this.handleChangeType}
								off={this.state.type === 'password'}
							/>
						</span>
					</InputBox>
					{this.state.error && <Error>Email e/ ou senha incorreta</Error>}
					{/* {this.state.error && <Error>Endereço de email inválido</Error> */}
					<Button
						login
						text="ENTRAR"
						type="submit"
					/>
					<Span>
						<buttonText>CRIAR CONTA</buttonText>
						<buttonText>RESETAR SENHA</buttonText>
					</Span>
				</Form>
			</ContainerForm>
		);
	}
}

export default Login;
