/* eslint-disable no-mixed-spaces-and-tabs */
// Libs
import React, { Component } from 'react';
import { connect } from 'react-redux';
import styled from 'styled-components';

// Components
import Button from '../../../components/Button';
import Input from '../../../components/Input';
import ImageLogo from '../../../components/ImageLogo';
import FisicalPersonSucess from './UserSucess';

// Images
import VisibilityOff from '../../../assets/visibility-off.svg';
import VisibilityOn from '../../../assets/visibility-on.svg';

// Redux
import { addNewUser } from '../../../dataflow/modules/sign-up-modules';

const mapStateToProps = state => ({
	signup: state.signup,
});

const mapDispatchToProps = dispatch => ({
	addNewUser: (user) => {
		dispatch(addNewUser(user));
	},
});

const Container = styled.div`
  width: 100%;
  min-height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #ffcfcd;

  @media(max-width: 648px){
    background: #FFF;
    height: auto;
  }
`;

const Form = styled.form`
  min-width: 32%;
  background-color: #fff;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 1rem 0;
  border-radius: 5px;
  box-shadow: 0 1px 2px #00000029;

  input {
    border: ${props => (props.withError === true ? '1px solid #f00' : '1px solid #ffcfcd;')};
  }

  @media (max-width: 768px) {
    margin-top: 1rem;
    margin-bottom: 0.5rem;
    padding: 0 2rem;
  }

  @media (max-width: 648px) {
    margin: 0;
		width: 100%;
  }
`;

const BlockSmallerInput = styled.span`
	width: 90%;
	display: flex;
	justify-content: space-between;
	position: relative;
`;

const BlockTitle = styled.span`
	width: 90%;
	display: flex;
	align-items: center;
	justify-content: flex-start;

	@media(max-width: 648px){
		width: 100%;
	}
`;

const TitleForm = styled.h1`
  font-size: 1.3rem;
  font-family: "Overpass", ExtraBold;
  text-transform: uppercase;
  margin: 1.5rem 0 1rem 1.17rem;
  align-self: flex-start;

  @media(max-width: 768px){
	  margin-left: 0.7rem;
  }

  @media (max-width: 648px) {
		margin: 2rem 0 1rem 0.25rem;
		font-size: 1.3rem;
	}
`;

const Label = styled.label`
  position: relative;
  width: 92%;
  display: flex;
  align-items: center;
  flex-direction: column;

	@media(max-width: 768px){
		width: 95%;
	}

  @media (max-width: 648px) {
		width: 95%;
		margin-left: 0.3rem;
  }
  @media(max-width: 425px){
		width: 100%;
  }
`;

const ParagraphInput = styled.p`
	align-self: flex-start;
	text-transform: uppercase;
	color: #85144b;
	font-size: 0.75rem;
	margin: 1.5rem 0 0.5rem 1.4rem;
	font-family: Overpass;
	font-weight: bold;

	@media (max-width: 425px) {
		text-align: left;
		margin-left: 0.2rem;
	}
`;

const ErrorMessage = styled.h6`
  font-size: 0.6rem;
  color: #f00;
  align-self: flex-start;
  font-weight: normal;
  margin: 0.5rem 0 0.5rem 0.8rem;

  @media (max-width: 425px) {
    margin: 0.5rem 0 0.5rem 0;
  }
`;

const ErrorEmpty = styled.h6`
  font-size: 0.7rem;
  color: #F00;
  align-self: flex-start;
  font-weight: normal;
  margin: 0.5rem 0 0.5rem 1.9rem;

  @media(max-width: 648px){
	  align-self: flex-start;
	  margin: 1rem 0 1rem 0.2rem;
  }
`;
const ImagePassword = styled.img`
  position: absolute;
