import React, { Component } from "react";

import { connect } from "react-redux";

import { Container } from "./styles";
import ModalTerms from "./ModalTerms";
import Formulario from "./Form";

const user = [
  {
    rg: "123456789",
    orgao: "Detran",
    uf: "RJ",
    cpf: "12345678901",
    email: "gabriel@test.com",
    telefone: "21999999999"
  }
];

class CreateFisicalPerson extends Component {
  state = {
    modalTerms: false
  };

  togglePassword = ev => {
    ev.preventDefault();
    this.setState({
      togglePassword: !this.state.togglePassword
    });
  };

  handleModal = () => {
    this.setState({
      modalTerms: !this.state.modal
    });
  };

  handleChange = ev => {
    this.setState({
      [ev.target.name]: ev.target.value
    });
  };

  handleSubmit = ev => {
    ev.preventDefault();
    console.log(user);

    if (
      this.state.rg.length < 9 ||
      this.state.rg.length > 9 ||
      this.state.rg !== user.rg
    ) {
      this.setState({
        isErrorRg: true
      });
    } else {
      this.setState({
        isErrorRg: false
      });
    }

    if (
      this.state.cpf.length < 11 ||
      this.state.cpf.length > 11 ||
      this.state.rg !== user.cpf
    ) {
      this.setState({
        isErrorCpf: true
      });
    } else {
      this.setState({
        isErrorCpf: false
      });
    }

    if (this.state.senha.length < 6) {
      this.setState({
        isErrorPassword: true
      });
    } else {
      this.setState({
        isErrorPassword: false
      });
    }

    this.setState({
      nome: "",
      sobrenome: "",
      rg: "",
      orgao: "",
      uf: "",
      nascimento: "",
      cpf: "",
      email: "",
      telefone: "",
      senha: ""
    });
  };

  render() {
    return (
      <Container>
        {this.state.modal === true ? (
          <ModalTerms handleModal={this.handleModal} />
        ) : (
          <Formulario handleModal={this.handleModal} />
        )}
      </Container>
    );
  }
}

export default CreateFisicalPerson;
