import React from 'react';

import ImageLogo from '../../components/ImageLogo';
import Input from '../../components/Input.js'

class App extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        value: '',
        nome: 'name@email.com',
        senha: 'insert password'
      };

    }

    handleChange(event) {
     this.setState({value: event.target.value});
     this.setState({value: event.target.senha});
    }

    // handleSubmit(event) {
    //   alert('Está faltando o @ ' + this.state.value);
    //   event.preventDefault();
    // }

    render() {
      return (
        <div className="container">
          <form className="box-form" onSubmit={this.handleSubmit}>
            <ImageLogo/>
            <label>
              <p className="box-name">E-mail</p>
              <Input className="input"
                type="text" 
                value={this.state.value} 
                onChange={this.handleChange} 
                placeholder={this.state.nome}
              />
            </label>
            <label>
              <p className="box-senha">Senha</p>
              <Input className="input"
                type="password" 
                value={this.state.value} 
                onChange={this.handleChange}
                placeholder={this.state.senha}
              />
            </label>
            <button className="button-entrar" type="submit" value="Entrar" />
            <div className="box-recuperacao">
              <p>CRIAR CONTA</p>
              <p>RESETAR SENHA</p>
            </div>
          </form>
        </div>

      );
    }
  }

  export default App;
