// Libs
import React, { Component } from 'react';
import styled from 'styled-components';
import { Redirect } from 'react-router-dom';

// Components
import ImageLogo from '../../../components/ImageLogo';
import Button from '../../../components/Button';

const Container = styled.div`
  width: 100%;
  height: 100vh;
  background-color: #FFCFCD;
  display: flex;
  align-items: center;
  flex-direction: column; 
  margin: 0;

  @media (max-width: 648px) {
		padding: 1rem;
		background-color: #fff;
		justify-content: center;
	}
`;

const Content = styled.div`
  width: 35%;
  height: 50vh;
  background-color: #fff;
  border-radius: 5px;
  display: flex;
  align-items: center;
  flex-direction: column;
  justify-content: space-evenly;


	@media (max-width: 1030px) {
    width: 50%
	}

	@media (max-width: 648px) {
    width: 80%;
		height: 79vh;
	}
`;

const Title = styled.h1`
  width: 65%;
  color: #85144B;
  font-size: 1rem;
  font-weight: bold;
  text-align: center;
  display: flex;
  align-items: center;

  @media (max-width: 648px) {
		width: 95%;
	}
`;

const Code = styled.p`
  color: #FF4136;
  font-size: 2.5rem;
  font-weight: bold;
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: Overpass;

  @media (max-width: 648px) {
    margin: 3rem;
  }
`;

const ContainerParagraph = styled.span` 
  width: 85%;
  color: #231F20;
  text-align: center;
  display: flex;
  align-items: center;
  justify-content: center;

  @media (max-width: 648px) {
    width: 100%;
  }
`;

const Paragraph = styled.span`
  font-size: 0.8rem;
  font-family: Overpass, Regular;
`;

class ResetPasswordCode extends Component {
	constructor(props) {
		super(props);
		this.state = {
			redirect: undefined,
		};
	}

handleRedirect = () => {
	this.setState({
		redirect: true,
	});
}

render() {
	return (
		<Container>
			<ImageLogo margin='3rem 0 2.5rem 0'/>
			<Content>
				<Title>Aqui está o seu código de redefinição de senha:</Title>
				<Code>210704</Code>
				<ContainerParagraph>
					<Paragraph>Uma redefinição de senha foi necessária para o seu endereço de e-mail: nome@email.com.
            Se você não realizou essa solicitação,
            pode acessar sua conta normalmente.
					</Paragraph>
				</ContainerParagraph>
				<Button
					width='75%'
					widthMobile='100%'
					marginMobile='3rem 0 2rem 0'
					text="redefinir senha"
					type="button"
					onClick={this.handleRedirect}
				/>
			</Content>
			{this.state.redirect && <Redirect to={'/newpassword'}/>}
		</Container>
	);
}
}

export default ResetPasswordCode;
