import Input from '../../../components/Input';
import Button from '../../../components/Button';
import ErroPassoword from './ErrorPassword';
import CreateFisicalPersonScreen from '../CreateFisicalPerson/CreateFisicalPersonScreen';
// import { InputBox } from './login';


export const ContainerForm = styled.div`
  height: 100vh;
  background-color: #FFCFCD;
  display: flex;
  align-items: center;
  flex-direction: column; 
  margin: 0;
  padding: 5rem;

	@media (max-width: 648px) {
			background-color: #fff;
			padding: 1rem;
			justify-content: center;
	 	}
	}
`;

export const Form = styled.form`
  width: 40%;
  height: 40vh;
  background-color: #fff;
  margin: 2rem;
  display: flex;
  align-items: center;
  flex-direction: column;
  justify-content: space-evenly;

  @media (max-width: 945px) {
			width: 55%;
	 	}
	}

	@media (max-width: 786px) {
			width: 70%;
	 	}
	}

	@media (max-width: 648px) {
			width: 90%;
			margin: 0;
	 	}
	}
`;

export const Title = styled.h1`
  width: 80%;
  font-size: 1.2rem;
  /* margin-top: 0.9rem; */
	font-family: Overpass, ExtraBold;
`;

export const Box = styled.span`
	width: 80%;
	display: flex;
	flex-direction: column;

	@media (max-width: 648px) {
			width: 100%;
	 	}
	}
`;

export const Label = styled.label`
  color: #85144B;
  font-size: 0.7rem;
  margin: 0.9rem;
  margin-top: 1rem;
  margin-bottom: 0.3rem;
	font-family: Overpass, Regular; 
`;

export const BoxButton = styled.div`
	width: 100%;
  margin-top: 0.5rem;

	Button {
		width: 95%;
		font-size: 1rem;
		font-family: Eurostile, Bold; 

		@media (max-width: 425px) {
			width: 100%;
	 	}
	}
`;

export const VoltarLogin = styled.span` 
  width: 95%;
  display: flex;
  align-items: center;
  flex-direction: row;
  justify-content: space-around;
  margin: 1rem;

	@media (max-width: 648px) {
			margin: 0;
	 	}
  }

  buttonText {
    color: #85144B;
    font-size: 1rem; 
		font-family: Eurostile, Regular; 
  }
`;

class ResetPasswordEmailScreen extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			value: '',
			email: '',
		};
	}

	handleChangeEmail = (ev) => {
		this.setState({
			email: ev.target.value,
		});
	};

	handleSubmit = () => {
		console.log('cliquei');
		return <Redirect to={errorPassword}/>
	}

	render() {
		return (
			<ContainerForm>
				<ImageLogo />
				<Form onSubmit={this.handleSubmit}>
					<Title>PASSWORD RESET</Title>
					<Box>
						<Label>E-MAIL</Label>
						<Input
							type="email"
							onChange={this.handleChangeEmail}
							placeholder="name@email.com"
						/>
						<BoxButton>
							<Button
								onClick={this.handleInsertCodeScreen}
								to={'/errorPassword'}
								text="REQUEST PASSWORD RESET"
								type= "submit"
							/>
						</BoxButton>
					</Box>
					<VoltarLogin>
						<buttonText>BACK TO LOGIN</buttonText>
					</VoltarLogin>
				</Form>
			</ContainerForm>
		);
	}
}

export default ResetPasswordEmailScreen;
