// Libs
import React from 'react';
import styled from 'styled-components';

// Components
import ImageLogo from '../../../components/ImageLogo';
import Input from '../../../components/Input';
import Button from '../../../components/Button';


export const ContainerForm = styled.div`
  height: 100vh;
  background-color: #FFCFCD;
  display: flex;
  align-items: center;
  flex-direction: column; 
  margin: 0;
  padding: 5rem;

`;

export const Form = styled.form`
  width: 40%;
  height: 40vh;
  background-color: #fff;
  margin: 2rem;
  display: flex;
  align-items: center;
  flex-direction: column;
  justify-content: space-evenly;
`;

export const Title = styled.h1`
  width: 80%;
  font-size: 1.2rem;
  /* margin-top: 0.9rem; */
	font-family: Overpass, ExtraBold;
`;

export const Box = styled.span`
	width: 80%;
	display: flex;
	flex-direction: column;

`;

export const Label = styled.label`
  color: #85144B;
  font-size: 0.7rem;
  margin: 0.9rem;
  margin-top: 1rem;
  margin-bottom: 0.3rem;
	font-family: Overpass, Regular; 
`;

export const BoxButton = styled.div`
	width: 100%;
  margin-top: 0.5rem;

	Button {
		width: 95%;
		font-size: 1rem;
		font-family: Eurostile, Bold; 

`;

export const VoltarLogin = styled.span` 
  width: 95%;
  display: flex;
  align-items: center;
  flex-direction: row;
  justify-content: space-around;
  margin: 1rem;

  buttonText {
    color: #85144B;
    font-size: 1rem; 
		font-family: Eurostile, Regular; 
  }
`;


class PasswordResetCodeScreen extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			value: '',
			email: '',
			password: '',
			type: 'password',
		};
	}

	handleChangeNumber = (ev) => {
		this.setState({
			number: ev.target.value,
		});
	};

	handleChangePassword = (ev) => {
		this.setState({
			password: ev.target.value,
		});
	}

	render() {
		return (
			<ContainerForm>
				<Form onSubmit={this.handleSubmit}>
					<ImageLogo />
					<Span>
						<Label>CONFIRMATION CODE</Label>
						<Input
							login
							onChange={this.handleChangeNumber}
							placeholder="Insert here the code"
						/>
					</Span>
					<Span>
						<Label>NEW PASSWORD</Label>
						<Input
							login
							type={this.state.type}
							onChange={this.handleChangePassword}
							placeholder="Insert here the code"
						/>
					</Span>
					<Span>
						<Label>REPEAT NEW PASSWORD</Label>
						<Input
							login
							type={this.state.type}
							onChange={this.handleChangePassword}
							placeholder="Insert here the code"
						/>
					</Span>
					<Button
						login
						text="PROCEED WITH NEW PASSWORD"
						type="submit"
					/>
					<Span>
						<buttonText>RESEND EMAIL</buttonText>
					</Span>
				</Form>
			</ContainerForm>
		);
	}
}

export default PasswordResetCodeScreen;
